pragma solidity ^0.4.17;

//All amounts in Wei NOT ether

contract EthRiddle {

	struct Riddle {
		address owner;
		bytes32 answer;
		uint guessCost;
		uint payoutAmt;
		uint guessTaxation;
		uint payoutTaxation;
		uint balance;
		bool active;
		bool hasPaidOut;
		bool hasCancelled;
	}

	mapping (bytes32 => Riddle) riddles;
	address private ownerOfEthRiddle;

	//Fallback function
	function() public payable { }


	function EthRiddle() public {
		ownerOfEthRiddle = msg.sender;
	}

	function kill() public {
		if (msg.sender == ownerOfEthRiddle) {
		selfdestruct(ownerOfEthRiddle);
		}
	}

	function createRiddle(string id, string answer, uint guessCost, uint payoutAmt, uint guessTaxation, uint payoutTaxation) public payable {
		bytes32 bytesId = keccak256(id);
		if (riddles[bytesId].owner == 0) {
			if (msg.value==payoutAmt){
				bytes32 hashedAnswer = keccak256(answer);
				Riddle memory riddle = Riddle(msg.sender, hashedAnswer, guessCost, payoutAmt, guessTaxation, payoutTaxation, msg.value, true, false, false);
				riddles[bytesId] = riddle;

			}
		}
	}

	function guess(string id, string guess) public payable {
		bytes32 bytesId = keccak256(id);
		Riddle memory riddle = riddles[bytesId];
		//if tx value sent is equal to guessCost and balance is sufficient for payout and the riddle hasn't paid out and the riddle hasn't cancelled and riddle is active
		if ((((msg.value==riddle.guessCost)&&(riddle.balance>=riddle.payoutAmt))
			&&((!riddle.hasPaidOut)&&(!riddle.hasCancelled)))
			&&(riddle.active)) {
			if (keccak256(guess)==riddle.answer) {
				riddles[bytesId].balance = riddles[bytesId].balance - riddle.payoutAmt;
				riddles[bytesId].balance = riddles[bytesId].balance + riddle.guessCost - riddle.guessTaxation;
				riddles[bytesId].hasPaidOut = true;
				riddles[bytesId].active = false;
				ownerOfEthRiddle.transfer(riddle.payoutTaxation+riddle.guessTaxation);
				msg.sender.transfer(riddle.payoutAmt-riddle.payoutTaxation);
			}
			else {
				riddles[bytesId].balance = riddles[bytesId].balance + riddle.guessCost - riddle.guessTaxation;
				ownerOfEthRiddle.transfer(riddle.guessTaxation); 
			}
		}
		else {
			msg.sender.transfer(msg.value);
		}
	}

	function withdrawFromRiddle(string id, uint amt) public payable {
		bytes32 bytesId = keccak256(id);
		Riddle memory riddle = riddles[bytesId];
		if (msg.sender==riddle.owner) {
			if (riddle.balance>=amt) {
				riddles[bytesId].balance = riddles[bytesId].balance - amt;
				if (riddles[bytesId].balance<riddle.payoutAmt){
					riddles[bytesId].active = false;
				}
				riddle.owner.transfer(amt);
			}
		}
	}

	function cancelRiddle(string id) public payable {
		bytes32 bytesId = keccak256(id);
		Riddle memory riddle = riddles[bytesId];
		if (msg.sender==riddle.owner) {
			riddles[bytesId].active = false;
			riddles[bytesId].hasCancelled = true;
		}
	}

	function checkForPaidOut(string id) public view returns (bool) {
		bytes32 bytesId = keccak256(id);
		Riddle memory riddle = riddles[bytesId];
		if (riddle.hasPaidOut) {
			return true;
		}
		else {
			return false;
		}
	}

	function checkForCancelled(string id) public view returns (bool) {
		bytes32 bytesId = keccak256(id);
		Riddle memory riddle = riddles[bytesId];
		if (riddle.hasCancelled) {
			return true;
		} 
		else {
			return false;
		}
	}

	function checkForActivity(string id) public view returns (bool){
		bytes32 bytesId = keccak256(id);
		Riddle memory riddle = riddles[bytesId];
		if (riddle.active) {
			return true;
		}
		else {
			return false;
		}
	}

	function getBalance(string id) public view returns (uint) {
		bytes32 bytesId = keccak256(id);
		Riddle memory riddle = riddles[bytesId];

		if (msg.sender==riddle.owner) {
			return riddle.balance;
		}
		else {
			return 0;
		}
	}

	function getOwnerOfRiddle(string id) public view returns (address) {
		bytes32 bytesId = keccak256(id);
		Riddle memory riddle = riddles[bytesId];
		return riddle.owner;
	}

	function getOwnerOfEthRiddle() public view returns (address) {
		return ownerOfEthRiddle;
	}

	function getEthRiddleBalance() public view returns (uint) {
		address myAddress = this;
		return myAddress.balance;
	}

}
